declare module "*.svg" {
    const content: any;
    export default content;
  }

  declare module "*.png" {
    const value: string;
    export default value;
  }

  declare module "*.scss" {
    const value: any;
    export default value;
  }

  declare module "*lodash.debounce" {
    const value: any;
    export default value;
  }

